-- Create Agents Table
CREATE TABLE Agents (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    contact_info VARCHAR(255)
);

-- Create Houses Table
CREATE TABLE Houses (
    id INT PRIMARY KEY,
    address VARCHAR(255),
    price DECIMAL,
    description TEXT,
    number_of_bedrooms INT,
    number_of_bathrooms INT,
    square_footage INT,
    agent_id INT,
    FOREIGN KEY (agent_id) REFERENCES Agents(id)
);

-- Create Clients Table
CREATE TABLE Clients (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    contact_info VARCHAR(255),
    preferred_location VARCHAR(255),
    budget DECIMAL
);

